tables.table_schema:
CREATE TABLE Inventory ( 
    product_id INT PRIMARY KEY, 
    product_name TEXT, 
    current_stock INT, 
    reorder_point INT 
);

CREATE TABLE SalesData ( 
    sale_id INT PRIMARY KEY, 
    product_id INT, 
    sale_date DATE, 
    sale_amount DECIMAL 
);

CREATE TABLE ReorderPoints ( 
    product_id INT PRIMARY KEY, 
    reorder_point INT 
);

CREATE TABLE ReplenishmentSchedules ( 
    product_id INT PRIMARY KEY, 
    replenishment_date DATE, 
    replenishment_quantity INT 
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name TEXT,
    address TEXT,
    phone_number TEXT
);

CREATE TABLE replenishment (
    replenishment_id INT PRIMARY KEY,
    product_id INT,
    quantity_added INT,
    replenishment_date DATE
);
