CREATE TABLE texts (
    text_id UUID PRIMARY KEY,
    text_source VARCHAR(255),
    text_content TEXT
);

CREATE TABLE language_models (
    model_id UUID PRIMARY KEY,
    model_name VARCHAR(255),
    model_description TEXT,
    trained_text_id UUID
);

CREATE TABLE texts (
    text_id INT PRIMARY KEY,
    text_content TEXT,
    source_name TEXT
);

CREATE TABLE authors (
    author_id INT PRIMARY KEY,
    author_name TEXT,
    author_bio TEXT
);

CREATE TABLE languages (
    language_id INT PRIMARY KEY,
    language_name TEXT
);

CREATE TABLE sources (
    source_id INT PRIMARY KEY,
    source_name TEXT,
    source_language_id INT,
    FOREIGN KEY (source_language_id) REFERENCES languages(language_id)
);

CREATE TABLE text_sources (
    id SERIAL PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL
);

CREATE TABLE texts (
    text_id SERIAL PRIMARY KEY,
    text_content TEXT NOT NULL,
    source_id INT,
    FOREIGN KEY (source_id) REFERENCES text_sources(id)
);

CREATE TABLE models (
    model_id SERIAL PRIMARY KEY,
    model_name VARCHAR(255) NOT NULL
);

CREATE TABLE train_logs (
    log_id SERIAL PRIMARY KEY,
    training_id INT,
    log_message TEXT,
    FOREIGN KEY (training_id) REFERENCES model_training(training_id)
);
