tables.table_schema:
CREATE TABLE language_rules (
   id INT PRIMARY KEY,
   rule_name VARCHAR(255),
   rule_description TEXT
);

CREATE TABLE text_inputs (
   input_id INT PRIMARY KEY,
   input_text TEXT,
   language_rule_id INT
);

CREATE TABLE language_rules (
    rule_id INT PRIMARY KEY,
    rule_description TEXT,
    rule_syntax TEXT
);

CREATE TABLE text_inputs (
    input_id INT PRIMARY KEY,
    input_text TEXT,
    input_language_id INT
);

CREATE TABLE language_keywords (
    keyword_id INT PRIMARY KEY,
    keyword TEXT,
    language_id INT
);

CREATE TABLE language_definitions (
    definition_id INT PRIMARY KEY,
    definition TEXT,
    example TEXT
);

CREATE TABLE languages (
    language_id INT PRIMARY KEY,
    language_name TEXT,
    grammar_rules JSONB
);

CREATE TABLE texts (
    text_id INT PRIMARY KEY,
    text_content TEXT
);

CREATE TABLE tokens (
    token_id INT PRIMARY KEY,
    token_name TEXT,
    token_type TEXT
);

CREATE TABLE interpretations (
    interpretation_id INT PRIMARY KEY,
    interpretation_text TEXT,
    rule_id INT REFERENCES rules(rule_id)
);
