tables.table_schema:
CREATE TABLE language_model_outputs (
    id INT PRIMARY KEY,
    generated_text TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE language_models (
    id INT PRIMARY KEY,
    model_name VARCHAR(255),
    model_description TEXT,
    trained_date DATE
);

CREATE TABLE generated_texts (
    text_id INT PRIMARY KEY,
    model_id INT,
    generated_text TEXT,
    generated_date TIMESTAMP
);

CREATE TABLE LanguageModels (
    id SERIAL PRIMARY KEY,
    model_name VARCHAR(50) NOT NULL,
    model_type VARCHAR(50) NOT NULL,
    accuracy DECIMAL(5,2) NOT NULL
);

CREATE TABLE TextSamples (
    id SERIAL PRIMARY KEY,
    text_content TEXT NOT NULL,
    text_length INT NOT NULL,
    text_source VARCHAR(100) NOT NULL
);

CREATE TABLE language_model_outputs (
    id SERIAL PRIMARY KEY,
    input_text TEXT,
    generated_text TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE training_data (
    data_id SERIAL PRIMARY KEY,
    input_data TEXT,
    output_data TEXT
);

CREATE TABLE models (
    model_id SERIAL PRIMARY KEY,
    model_name TEXT,
    description TEXT,
    created_at TIMESTAMP
);

CREATE TABLE model_parameters (
    parameter_id SERIAL PRIMARY KEY,
    model_id INT REFERENCES models(model_id),
    parameter_name TEXT,
    parameter_value FLOAT
);

CREATE TABLE LanguageModels (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255),
    description TEXT,
    creation_date TIMESTAMP
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    registration_date TIMESTAMP
);

CREATE TABLE TrainingData (
    data_id INT PRIMARY KEY,
    text_id INT,
    training_date TIMESTAMP,
    FOREIGN KEY (text_id) REFERENCES GeneratedTexts(text_id)
);
