tables.table_schema:
CREATE TABLE Corpus (
    corpus_id INT PRIMARY KEY,
    corpus_name VARCHAR(255) NOT NULL,
    language VARCHAR(50) NOT NULL
);

CREATE TABLE Document (
    document_id INT PRIMARY KEY,
    document_name VARCHAR(255) NOT NULL,
    corpus_id INT,
    FOREIGN KEY (corpus_id) REFERENCES Corpus(corpus_id)
);

CREATE TABLE Corpus (
    corpus_id INT PRIMARY KEY,
    corpus_name VARCHAR,
    language VARCHAR
);

CREATE TABLE Document (
    document_id INT PRIMARY KEY,
    document_name VARCHAR,
    corpus_id INT,
    FOREIGN KEY (corpus_id) REFERENCES Corpus(corpus_id)
);

CREATE TABLE Sentence (
    sentence_id INT PRIMARY KEY,
    sentence_text TEXT,
    document_id INT,
    FOREIGN KEY (document_id) REFERENCES Document(document_id)
);

CREATE TABLE linguistic_corpus (
    id SERIAL PRIMARY KEY,
    text TEXT,
    language TEXT
);

CREATE TABLE authors (
    id SERIAL PRIMARY KEY,
    name TEXT,
    affiliation TEXT
);

CREATE TABLE publications (
    id SERIAL PRIMARY KEY,
    title TEXT,
    publication_date DATE
);

CREATE TABLE analysis_results (
    id SERIAL PRIMARY KEY,
    analysis_type TEXT,
    result JSONB
);

CREATE TABLE corpora (
    corpus_id UUID PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    language VARCHAR(50),
    date_created TIMESTAMP
);

CREATE TABLE texts (
    text_id UUID PRIMARY KEY,
    corpus_id UUID REFERENCES corpora(corpus_id),
    author VARCHAR(100),
    content TEXT
);

CREATE TABLE sentences (
    sentence_id UUID PRIMARY KEY,
    text_id UUID REFERENCES texts(text_id),
    sentence_number INT,
    sentence_content TEXT
);

CREATE TABLE words (
    word_id UUID PRIMARY KEY,
    sentence_id UUID REFERENCES sentences(sentence_id),
    word_text VARCHAR(50),
    part_of_speech VARCHAR(50)
);

CREATE TABLE categories (
    category_id UUID PRIMARY KEY,
    word_id UUID REFERENCES words(word_id),
    category_name VARCHAR(50)
);
