tables.table_schema:
CREATE TABLE word_frequency (
    id INT PRIMARY KEY,
    word VARCHAR(255),
    frequency INT
);

CREATE TABLE word_frequencies (
  word VARCHAR(255),
  frequency INT
);

CREATE TABLE word_analysis (
  analysis_id UUID PRIMARY KEY,
  word VARCHAR(255),
  analysis_result TEXT
);

CREATE TABLE WordFrequencyCounts (
   word VARCHAR(255) NOT NULL,
   frequency INT NOT NULL
);

CREATE TABLE DocumentInformation (
   document_id INT PRIMARY KEY,
   title VARCHAR(255) NOT NULL,
   author VARCHAR(255),
   date_published DATE
);

CREATE TABLE AnalysisResults (
   document_id INT,
   word VARCHAR(255),
   frequency INT,
   FOREIGN KEY (document_id) REFERENCES DocumentInformation(document_id)
);

CREATE TABLE word_frequency (
    word_id SERIAL PRIMARY KEY,
    word TEXT NOT NULL,
    frequency INT NOT NULL
);

CREATE TABLE document (
    document_id SERIAL PRIMARY KEY,
    document_name TEXT NOT NULL
);

CREATE TABLE document_word (
    document_id INT REFERENCES document(document_id),
    word_id INT REFERENCES word_frequency(word_id),
    count INT
);

CREATE TABLE visualization (
    visualization_id SERIAL PRIMARY KEY,
    document_id INT REFERENCES document(document_id),
    visualization_type TEXT NOT NULL
);
