tables.table_schema:
CREATE TABLE translations (
    id INT PRIMARY KEY,
    source_language VARCHAR,
    target_language VARCHAR,
    text TEXT
);

CREATE TABLE translations (
    translation_id UUID PRIMARY KEY,
    source_language TEXT,
    target_language TEXT,
    translated_text TEXT
);

CREATE TABLE translation_history (
    history_id SERIAL PRIMARY KEY,
    translation_id UUID REFERENCES translations (translation_id),
    translation_date TIMESTAMP,
    translation_status TEXT
);

CREATE TABLE languages (
    language_id SERIAL PRIMARY KEY,
    language_name TEXT
);

CREATE TABLE Languages (
    language_id INT PRIMARY KEY,
    language_name VARCHAR(50) NOT NULL
);

CREATE TABLE TranslationPairs (
    pair_id INT PRIMARY KEY,
    source_language_id INT REFERENCES Languages(language_id),
    target_language_id INT REFERENCES Languages(language_id),
    translation_text TEXT NOT NULL
);

CREATE TABLE Models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(50) NOT NULL,
    source_language_id INT REFERENCES Languages(language_id),
    target_language_id INT REFERENCES Languages(language_id)
);

CREATE TABLE TrainingData (
    data_id INT PRIMARY KEY,
    model_id INT REFERENCES Models(model_id),
    pair_id INT REFERENCES TranslationPairs(pair_id)
);

CREATE TABLE Metrics (
    metric_id INT PRIMARY KEY,
    model_id INT REFERENCES Models(model_id),
    metric_name VARCHAR(50) NOT NULL,
    metric_value FLOAT NOT NULL
);
