tables.table_schema:
CREATE TABLE data_sources (
    data_source_id INT PRIMARY KEY,
    data_source_name VARCHAR(255) NOT NULL,
    data_source_type VARCHAR(50) NOT NULL
);

CREATE TABLE integrated_data (
    integration_id INT PRIMARY KEY,
    data_source_id INT NOT NULL,
    integration_date DATE NOT NULL,
    integrated_data JSONB
);

CREATE TABLE source1 (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    age INT,
    gender VARCHAR(10)
);

CREATE TABLE source2 (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    revenue DECIMAL(10, 2)
);

CREATE TABLE source1 (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    data_entry_date DATE
);

CREATE TABLE source2 (
    id INT PRIMARY KEY,
    email VARCHAR(255),
    phone_number VARCHAR(15)
);

CREATE TABLE integrated_data (
    id INT PRIMARY KEY,
    source1_id INT REFERENCES source1(id),
    source2_id INT REFERENCES source2(id),
    integration_date DATE
);

CREATE TABLE business_insights (
    id INT PRIMARY KEY,
    insights_text TEXT,
    insights_date DATE
);
