tables.table_schema:
CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username STRING,
    age INT,
    gender STRING
);

CREATE TABLE user_activity (
  user_id INT PRIMARY KEY,
  activity_type VARCHAR(50),
  duration_minutes INT,
  calories_burned INT
);

CREATE TABLE heart_rate_data (
  user_id INT,
  timestamp TIMESTAMP,
  heart_rate INT,
  FOREIGN KEY (user_id) REFERENCES user_activity(user_id)
);
