tables.table_schema:
CREATE TABLE symptoms (
    symptom_id INT PRIMARY KEY,
    symptom_name VARCHAR(50)
);

CREATE TABLE Symptoms (
  symptom_id INT PRIMARY KEY,
  symptom_name VARCHAR(50),
  description TEXT
);

CREATE TABLE Diagnoses (
  diagnosis_id INT PRIMARY KEY,
  diagnosis_name VARCHAR(50),
  patient_id INT,
  diagnosis_date DATE
);
