tables.table_schema:
CREATE TABLE Clinicians (
    ClinicianID INT,
    ClinicianName TEXT,
    Specialization TEXT,
    Location TEXT
);

CREATE TABLE Patient (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50),
    date_of_birth DATE,
    gender VARCHAR(10)
);

CREATE TABLE Radiologist (
    radiologist_id INT PRIMARY KEY,
    radiologist_name VARCHAR(50),
    specialty VARCHAR(50)
);
