tables.table_schema:
CREATE TABLE maintenance_logs (
	device_id INT NOT NULL,
	log_timestamp TIMESTAMP NOT NULL,
	maintenance_type VARCHAR(255) NOT NULL,
	maintenance_description TEXT NOT NULL
);

CREATE TABLE maintenance_logs (
    log_id INT PRIMARY KEY,
    device_id INT,
    log_description TEXT,
    maintenance_date DATE
);

CREATE TABLE maintenance_schedules (
    schedule_id INT PRIMARY KEY,
    device_id INT,
    maintenance_type TEXT,
    frequency INT
);

CREATE TABLE DeviceMaintenanceLogs (
    log_id INT PRIMARY KEY,
    device_id INT,
    maintenance_date DATE,
    maintenance_description VARCHAR(255)
);

CREATE TABLE DeviceSchedules (
    schedule_id INT PRIMARY KEY,
    device_id INT,
    scheduled_date DATE,
    scheduled_maintenance_type VARCHAR(100)
);

CREATE TABLE MedicalDevices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(100),
    device_type VARCHAR(100),
    last_maintenance_date DATE
);

CREATE TABLE Device (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255) NOT NULL,
    maintenance_schedule VARCHAR(255) NOT NULL
);

CREATE TABLE Technician (
    technician_id INT PRIMARY KEY,
    technician_name VARCHAR(100) NOT NULL,
    specialization VARCHAR(100) NOT NULL
);

CREATE TABLE Device (
  device_id UUID PRIMARY KEY,
  device_name VARCHAR(255) NOT NULL,
  device_type VARCHAR(100) NOT NULL,
  device_location VARCHAR(100) NOT NULL
);

CREATE TABLE MaintenanceLog (
  log_id UUID PRIMARY KEY,
  device_id UUID REFERENCES Device(device_id),
  maintenance_date DATE NOT NULL,
  maintenance_description TEXT NOT NULL
);

CREATE TABLE Technician (
  technician_id UUID PRIMARY KEY,
  technician_name VARCHAR(255) NOT NULL,
  technician_experience_years INT NOT NULL
);

CREATE TABLE MaintenanceAssignment (
  assignment_id UUID PRIMARY KEY,
  technician_id UUID REFERENCES Technician(technician_id),
  schedule_id UUID REFERENCES MaintenanceSchedule(schedule_id)
);
