tables.table_schema:
CREATE TABLE regulatory_audits (
    audit_id SERIAL PRIMARY KEY,
    audit_date DATE,
    auditor_name VARCHAR(255),
    audit_findings TEXT
);

CREATE TABLE inspections (
    inspection_id SERIAL PRIMARY KEY,
    inspection_date DATE,
    inspector_name VARCHAR(255),
    inspection_result TEXT
);

CREATE TABLE audits (
   audit_id INT PRIMARY KEY,
   audit_date DATE,
   auditor_name VARCHAR(50),
   audit_result VARCHAR(100)
);

CREATE TABLE inspections (
   inspection_id INT PRIMARY KEY,
   inspection_date DATE,
   inspector_name VARCHAR(50),
   inspection_report TEXT
);

CREATE TABLE compliance_records (
   record_id INT PRIMARY KEY,
   company_name VARCHAR(100),
   compliance_status VARCHAR(50),
   compliance_date DATE
);

CREATE TABLE inspections (
    inspection_id UUID PRIMARY KEY,
    location TEXT,
    inspector_name TEXT,
    inspection_date DATE
);

CREATE TABLE findings (
    finding_id SERIAL PRIMARY KEY,
    inspection_id UUID REFERENCES inspections(inspection_id),
    finding_description TEXT,
    compliance_status BOOLEAN
);

CREATE TABLE violations (
    violation_id SERIAL PRIMARY KEY,
    finding_id INT REFERENCES findings(finding_id),
    violation_type TEXT,
    penalty_amount DECIMAL
);

CREATE TABLE corrective_actions (
    action_id SERIAL PRIMARY KEY,
    finding_id INT REFERENCES findings(finding_id),
    action_description TEXT,
    completion_date DATE
);
