tables.table_schema:
CREATE TABLE imaging_data (
  image_id UUID PRIMARY KEY,
  patient_id UUID REFERENCES patients(id),
  image_data BYTEA,
  date_taken timestamp
);

CREATE TABLE imaging_data (
    image_id UUID PRIMARY KEY,
    image_path VARCHAR(255) NOT NULL,
    patient_id UUID,
    scan_date DATE
);

CREATE TABLE ml_model (
    model_id UUID PRIMARY KEY,
    model_name VARCHAR(50) NOT NULL,
    training_data_table VARCHAR(50) NOT NULL,
    trained_on_date DATE
);

CREATE TABLE Images (
  image_id UUID PRIMARY KEY,
  image_path VARCHAR(255) NOT NULL,
  image_data BYTEA NOT NULL,
  disease_label VARCHAR(50) NOT NULL
);

CREATE TABLE Patients (
  patient_id UUID PRIMARY KEY,
  patient_name VARCHAR(100) NOT NULL,
  patient_age INT NOT NULL,
  patient_gender VARCHAR(10) NOT NULL
);

CREATE TABLE Predictions (
  prediction_id UUID PRIMARY KEY,
  image_id UUID REFERENCES Images(image_id),
  prediction_result VARCHAR(50) NOT NULL,
  prediction_date TIMESTAMP NOT NULL
);

CREATE TABLE images (
    image_id UUID PRIMARY KEY,
    image_data BYTEA,
    patient_id UUID,
    image_date TIMESTAMP
);

CREATE TABLE patients (
    patient_id UUID PRIMARY KEY,
    patient_name VARCHAR(50),
    patient_age INT,
    patient_gender VARCHAR(10)
);

CREATE TABLE diseases (
    disease_id UUID PRIMARY KEY,
    disease_name VARCHAR(50),
    disease_description TEXT
);

CREATE TABLE Images (
    image_id INT PRIMARY KEY,
    image_data BYTEA,
    patient_id INT,
    date_uploaded DATE
);

CREATE TABLE Patients (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255),
    date_of_birth DATE,
    gender VARCHAR(10)
);

CREATE TABLE Diagnoses (
    diagnosis_id INT PRIMARY KEY,
    patient_id INT,
    diagnosis_description TEXT,
    diagnosis_date DATE
);

CREATE TABLE AI_Models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255),
    model_type VARCHAR(50),
    training_accuracy FLOAT
);
