tables.table_schema:
CREATE TABLE genetic_sequences (
    sequence_id INT PRIMARY KEY,
    sequence_name VARCHAR(255),
    gene_name VARCHAR(255),
    dna_sequence TEXT
);

CREATE TABLE Patient (
    patient_id INT PRIMARY KEY,
    name VARCHAR,
    age INT,
    gender VARCHAR
);

CREATE TABLE Sample (
    sample_id INT PRIMARY KEY,
    patient_id INT,
    type VARCHAR,
    quantity INT
);

CREATE TABLE Gene (
    gene_id INT PRIMARY KEY,
    name VARCHAR,
    sequence TEXT
);

CREATE TABLE Variant (
    variant_id INT PRIMARY KEY,
    gene_id INT,
    type VARCHAR,
    frequency FLOAT
);

CREATE TABLE Analysis (
    analysis_id INT PRIMARY KEY,
    sample_id INT,
    variant_id INT,
    result TEXT
);
