tables.table_schema:
CREATE TABLE vital_signs (
  patient_id INT PRIMARY KEY,
  heart_rate INT,
  blood_pressure VARCHAR(20),
  temperature FLOAT
);

CREATE TABLE device_info (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  location VARCHAR(100),
  last_updated TIMESTAMP
);

CREATE TABLE patient_data (
  patient_id INT PRIMARY KEY,
  temperature FLOAT,
  heart_rate INT,
  blood_pressure VARCHAR
);

CREATE TABLE device (
  device_id INT PRIMARY KEY,
  device_name VARCHAR,
  location VARCHAR,
  patient_id INT REFERENCES patient_data(patient_id)
);

CREATE TABLE alert (
  alert_id INT PRIMARY KEY,
  alert_description VARCHAR,
  timestamp TIMESTAMP,
  patient_id INT REFERENCES patient_data(patient_id)
);

CREATE TABLE devices (
    id SERIAL PRIMARY KEY,
    device_id VARCHAR(20) UNIQUE,
    location VARCHAR(50),
    status VARCHAR(20)
);

CREATE TABLE alerts (
  id INT PRIMARY KEY,
  patient_id INT,
  alert_message TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE locations (
  id INT PRIMARY KEY,
  location_name VARCHAR(100),
  latitude FLOAT,
  longitude FLOAT
);
