CREATE TABLE disease_prevalence (
    disease_id INT PRIMARY KEY,
    disease_name VARCHAR(255),
    prevalence_rate FLOAT,
    outbreak_pattern TEXT
);

CREATE TABLE research_data (
    research_id INT PRIMARY KEY,
    researcher_name VARCHAR(255),
    research_topic VARCHAR(255),
    research_date DATE
);

CREATE TABLE diseases (id INT PRIMARY KEY,
 name VARCHAR(255) NOT NULL,
 prevalence FLOAT NOT NULL);

CREATE TABLE researchers (id INT PRIMARY KEY,
 name VARCHAR(255) NOT NULL,
 institution VARCHAR(255) NOT NULL);

CREATE TABLE diseases (
    disease_id INT PRIMARY KEY,
    disease_name VARCHAR(255) NOT NULL,
    prevalence_rate DECIMAL(5,2) NOT NULL
);

CREATE TABLE outbreaks (
    outbreak_id INT PRIMARY KEY,
    disease_id INT REFERENCES diseases(disease_id),
    outbreak_date DATE NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(255) NOT NULL,
    area_of_study VARCHAR(255) NOT NULL
);

CREATE TABLE DiseasePrevalence (
    prevalence_id SERIAL PRIMARY KEY,
    disease_name VARCHAR(50),
    prevalence_percentage DECIMAL(5,2),
    year INT
);

CREATE TABLE Researchers (
    researcher_id SERIAL PRIMARY KEY,
    name VARCHAR(50),
    institution VARCHAR(100),
    specialty VARCHAR(50)
);
