tables.table_schema:
CREATE TABLE dna_sequences (
  id SERIAL PRIMARY KEY,
  sequence TEXT NOT NULL,
  label TEXT
);

CREATE TABLE sequence_analysis (
  analysis_id SERIAL PRIMARY KEY,
  sequence_id INT REFERENCES dna_sequences(id),
  analysis_result BOOLEAN
);

CREATE TABLE dna_sequences (
  id SERIAL PRIMARY KEY,
  sequence_id INT NOT NULL,
  dna_sequence TEXT NOT NULL
);

CREATE TABLE sequence_metadata (
  id SERIAL PRIMARY KEY,
  sequence_id INT NOT NULL,
  description TEXT NOT NULL,
  creator VARCHAR(255) NOT NULL
);

CREATE TABLE sequence_analysis (
  id SERIAL PRIMARY KEY,
  sequence_id INT NOT NULL,
  analysis_result JSONB
);

CREATE TABLE dna_sequences (
  id INT PRIMARY KEY,
  sequence VARCHAR(255)
);

CREATE TABLE genes (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(50),
  sequence_id INT,
  FOREIGN KEY (sequence_id) REFERENCES dna_sequences(id)
);

CREATE TABLE mutations (
  mutation_id INT PRIMARY KEY,
  gene_id INT,
  mutation_type VARCHAR(50),
  FOREIGN KEY (gene_id) REFERENCES genes(gene_id)
);

CREATE TABLE mutations_info (
  mutation_id INT,
  mutation_date DATE,
  mutation_details VARCHAR(255),
  FOREIGN KEY (mutation_id) REFERENCES mutations(mutation_id)
);

CREATE TABLE dna_sequences (
  sequence_id INT PRIMARY KEY,
  sequence VARCHAR(255)
);

CREATE TABLE sequence_metadata (
  sequence_id INT PRIMARY KEY,
  organism VARCHAR(100),
  length INT,
  creation_date DATE
);

CREATE TABLE sequence_annotations (
  annotation_id INT PRIMARY KEY,
  sequence_id INT,
  annotation VARCHAR(255),
  annotation_type VARCHAR(50)
);

CREATE TABLE sequence_similarity (
  similarity_id INT PRIMARY KEY,
  sequence_id INT,
  similar_sequence_id INT,
  similarity_score FLOAT
);
