tables.table_schema:
CREATE TABLE genetic_information (
  genetic_id UUID PRIMARY KEY,
  patient_id UUID REFERENCES patient_data(patient_id),
  genetic_sequence TEXT,
  gene_variants JSON
);

CREATE TABLE Patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(50),
  age INT,
  gender VARCHAR(10)
);

CREATE TABLE GeneticInfo (
  genetic_id INT PRIMARY KEY,
  patient_id INT,
  genetic_data JSON,
  CONSTRAINT fk_genetic_patient FOREIGN KEY (patient_id) REFERENCES Patients(patient_id)
);

CREATE TABLE TreatmentOptions (
  option_id INT PRIMARY KEY,
  patient_id INT,
  treatment_option VARCHAR(100),
  CONSTRAINT fk_treatment_patient FOREIGN KEY (patient_id) REFERENCES Patients(patient_id)
);
