tables.table_schema:
CREATE TABLE gene_expression_tracking (
  time_stamp TIMESTAMP,
  gene_id INT,
  expression_level FLOAT
);

CREATE TABLE gene_metadata (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(255),
  gene_description TEXT
);

CREATE TABLE gene_expression (
    gene_id INT PRIMARY KEY,
    expression_level FLOAT,
    timepoint TIMESTAMP
);

CREATE TABLE gene_annotations (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(50),
    description TEXT
);

CREATE TABLE timepoints (
    timepoint_id INT PRIMARY KEY,
    timepoint_name VARCHAR(50),
    timepoint_date DATE
);

CREATE TABLE GeneExpression ( 
   gene_id INT PRIMARY KEY, 
   expression_level FLOAT, 
   time_point TIMESTAMP 
);

CREATE TABLE GeneInfo ( 
   gene_id INT PRIMARY KEY, 
   gene_name TEXT, 
   gene_description TEXT 
);

CREATE TABLE TimePoint ( 
   time_point_id INT PRIMARY KEY, 
   time_description TEXT 
);

CREATE TABLE ExperimentLog ( 
   log_id INT PRIMARY KEY, 
   experiment_date DATE, 
   researcher_name TEXT 
);
