tables.table_schema:
CREATE TABLE PopulationGeneticVariations (
  population_id INT PRIMARY KEY,
  genetic_variation_id INT,
  allele VARCHAR(10),
  frequency DECIMAL
);

CREATE TABLE Population (id INT PRIMARY KEY,
name VARCHAR(100),
genetic_variation VARCHAR(100));

CREATE TABLE population_genes (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(50),
    gene_sequence TEXT,
    population_id INT
);

CREATE TABLE population_data (
    population_id INT PRIMARY KEY,
    population_name VARCHAR(50),
    country VARCHAR(50),
    region VARCHAR(50)
);

CREATE TABLE gene_variations (
    variation_id INT PRIMARY KEY,
    gene_id INT,
    variation_description TEXT,
    variation_type VARCHAR(50)
);

CREATE TABLE population_genotypes (
    genotype_id INT PRIMARY KEY,
    population_id INT,
    gene_id INT,
    genotype VARCHAR(10)
);

CREATE TABLE Population (
  population_id INT PRIMARY KEY,
  population_name VARCHAR(50) NOT NULL
);

CREATE TABLE Individual (
  individual_id INT PRIMARY KEY,
  individual_name VARCHAR(50) NOT NULL,
  population_id INT,
  FOREIGN KEY (population_id) REFERENCES Population(population_id)
);

CREATE TABLE GenomicData (
  data_id INT PRIMARY KEY,
  individual_id INT,
  genome_data BYTEA NOT NULL,
  FOREIGN KEY (individual_id) REFERENCES Individual(individual_id)
);

CREATE TABLE Variant (
  variant_id INT PRIMARY KEY,
  variant_name VARCHAR(50) NOT NULL,
  genomic_data_id INT,
  FOREIGN KEY (genomic_data_id) REFERENCES GenomicData(data_id)
);

CREATE TABLE Genes (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(50) NOT NULL,
  variant_id INT,
  FOREIGN KEY (variant_id) REFERENCES Variant(variant_id)
);
