tables.table_schema:
CREATE TABLE GeneticMarkers (
    marker_id INT PRIMARY KEY,
    marker_name VARCHAR(50),
    chromosome VARCHAR(10),
    position INT
);

CREATE TABLE genetic_markers (
  marker_id INT PRIMARY KEY,
  marker_name VARCHAR(50) NOT NULL,
  chromosome VARCHAR(10),
  position INT
);

CREATE TABLE sample_population (
  sample_id INT PRIMARY KEY,
  population_name VARCHAR(50) NOT NULL,
  country VARCHAR(50),
  region VARCHAR(50)
);

CREATE TABLE SamplePopulation (
  id SERIAL PRIMARY KEY,
  population_name VARCHAR(50) NOT NULL
);
