tables.table_schema:
CREATE TABLE GenomicsData (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(255),
  pathway_id INT,
  pathway_name VARCHAR(255)
);

CREATE TABLE genomics_data (
    sample_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    gene_expression FLOAT,
    pathway_id INT
);

CREATE TABLE drug_targets (
    target_id INT PRIMARY KEY,
    target_name VARCHAR(255),
    pathway_id INT
);

CREATE TABLE genetic_pathways (
    pathway_id INT PRIMARY KEY,
    pathway_name VARCHAR(255)
);

CREATE TABLE GeneticPathways (
  pathway_id INT PRIMARY KEY,
  pathway_name VARCHAR(255) NOT NULL
);

CREATE TABLE Genes (
  gene_id INT PRIMARY KEY,
  gene_name VARCHAR(255) NOT NULL,
  pathway_id INT,
  FOREIGN KEY (pathway_id) REFERENCES GeneticPathways(pathway_id)
);

CREATE TABLE DrugTargets (
  target_id INT PRIMARY KEY,
  target_name VARCHAR(255) NOT NULL,
  gene_id INT,
  FOREIGN KEY (gene_id) REFERENCES Genes(gene_id)
);

CREATE TABLE Drugs (
  drug_id INT PRIMARY KEY,
  drug_name VARCHAR(255) NOT NULL,
  target_id INT,
  FOREIGN KEY (target_id) REFERENCES DrugTargets(target_id)
);

CREATE TABLE gene_expression (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    expression_level FLOAT
);

CREATE TABLE pathway (
    pathway_id INT PRIMARY KEY,
    pathway_name VARCHAR(255),
    description TEXT
);

CREATE TABLE drug (
    drug_id INT PRIMARY KEY,
    drug_name VARCHAR(255),
    target_gene_id INT
);

CREATE TABLE genetic_variant (
    variant_id INT PRIMARY KEY,
    gene_id INT,
    variant_type VARCHAR(50),
    effect VARCHAR(50)
);

CREATE TABLE drug_target (
    target_id INT PRIMARY KEY,
    drug_id INT,
    gene_id INT
);
