tables.table_schema:
CREATE TABLE genetic_profiles (
  id UUID PRIMARY KEY,
  name TEXT NOT NULL,
  gender TEXT,
  age INT
);

CREATE TABLE medical_records (
  profile_id UUID REFERENCES genetic_profiles(id),
  record_id UUID PRIMARY KEY,
  diagnosis TEXT NOT NULL
);

CREATE TABLE GeneticProfiles (
	patient_id INT PRIMARY KEY,
	patient_name VARCHAR(50),
	genetic_code TEXT
);

CREATE TABLE Diseases (
	disease_id INT PRIMARY KEY,
	disease_name VARCHAR(50),
	severity ENUM('mild', 'moderate', 'severe')
);

CREATE TABLE Medications (
	medication_id INT PRIMARY KEY,
	medication_name VARCHAR(50),
	target_disease_id INT,
	FOREIGN KEY (target_disease_id) REFERENCES Diseases(disease_id)
);

CREATE TABLE medical_history (
	patient_id INT,
		condition VARCHAR(50),
		date_diagnosed DATE,
		medications JSON
);

CREATE TABLE genetic_profiles (
    profile_id INT PRIMARY KEY,
    patient_name TEXT,
    patient_dob DATE,
    genetic_data BYTEA
);

CREATE TABLE research_studies (
    study_id INT PRIMARY KEY,
    study_name TEXT,
    study_description TEXT
);

CREATE TABLE researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name TEXT,
    specialty TEXT,
    email TEXT
);
