tables.table_schema:
CREATE TABLE robots (
    robot_id SERIAL PRIMARY KEY,
    robot_name VARCHAR(50) NOT NULL
);

CREATE TABLE surgeries (
    surgery_id SERIAL PRIMARY KEY,
    robot_id INT NOT NULL,
    surgery_type VARCHAR(50) NOT NULL,
    surgery_date DATE
);

CREATE TABLE surgery_notes (
    note_id UUID PRIMARY KEY,
    surgery_id UUID,
    note_text TEXT,
    note_date TIMESTAMP
);

CREATE TABLE surgery_outcomes (
    outcome_id UUID PRIMARY KEY,
    surgery_id UUID,
    survival BOOLEAN,
    recovery_time_days INT
);
