tables.table_schema:
CREATE TABLE medical_supplies_inventory (
    supply_id INT PRIMARY KEY,
    supply_name TEXT NOT NULL,
    quantity INT NOT NULL,
    unit_price DECIMAL NOT NULL
);

CREATE TABLE medical_supplies (
  supply_id INT PRIMARY KEY,
  supply_name VARCHAR(50) NOT NULL,
  quantity_in_stock INT,
  unit_price DECIMAL
);

CREATE TABLE robotics_procedures (
  procedure_id INT PRIMARY KEY,
  procedure_name VARCHAR(100) NOT NULL,
  required_supplies TEXT[],
  duration_minutes INT
);

CREATE TABLE medical_supplies (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    quantity INT,
    expiration_date DATE
);

CREATE TABLE robotics_procedures (
    procedure_id INT PRIMARY KEY,
    procedure_name VARCHAR(100),
    equipment_used TEXT,
    supplies_required TEXT
);

CREATE TABLE inventory (
    inventory_id INT PRIMARY KEY,
    medical_supply VARCHAR(50) NOT NULL,
    quantity_in_stock INT NOT NULL,
    last_updated TIMESTAMP
);

CREATE TABLE robotics_procedures (
    procedure_id INT PRIMARY KEY,
    procedure_name VARCHAR(50) NOT NULL,
    equipment_used VARCHAR(100) NOT NULL,
    duration_hours DECIMAL(5,2) NOT NULL
);

CREATE TABLE procedure_inventory (
    procedure_id INT,
    inventory_id INT,
    FOREIGN KEY (procedure_id) REFERENCES robotics_procedures (procedure_id),
    FOREIGN KEY (inventory_id) REFERENCES inventory (inventory_id)
);

CREATE TABLE robot_operators (
    operator_id INT PRIMARY KEY,
    operator_name VARCHAR(50) NOT NULL,
    operator_role VARCHAR(50) NOT NULL
);

CREATE TABLE medical_supplies (
    id INT PRIMARY KEY,
    supply_name VARCHAR(255) NOT NULL,
    quantity INT NOT NULL,
    expiry_date DATE
);

CREATE TABLE hospitals (
    id INT PRIMARY KEY,
    hospital_name VARCHAR(255) NOT NULL,
    location VARCHAR(255)
);

CREATE TABLE procedures (
    id INT PRIMARY KEY,
    procedure_name VARCHAR(255) NOT NULL,
    equipment_required VARCHAR(255)
);

CREATE TABLE robots (
    id INT PRIMARY KEY,
    robot_name VARCHAR(255) NOT NULL,
    robot_type VARCHAR(255)
);

CREATE TABLE robot_inventory (
    id INT PRIMARY KEY,
    robot_id INT NOT NULL,
    supply_id INT NOT NULL,
    quantity INT NOT NULL
);
