tables.table_schema:
CREATE TABLE patient_consent_forms (
    id INT PRIMARY KEY,
    patient_id INT,
    consent_text TEXT,
    date_signed TIMESTAMP
);

CREATE TABLE documentation (
    doc_id INT PRIMARY KEY,
    patient_id INT,
    doc_type TEXT,
    doc_content TEXT
);

CREATE TABLE patient_consent_forms (
    patient_id INT PRIMARY KEY,
    form_name VARCHAR(255),
    consent_date DATE,
    signed BOOLEAN
);

CREATE TABLE regulations_compliance (
    rule_id INT PRIMARY KEY,
    rule_name VARCHAR(255),
    description TEXT
);

CREATE TABLE Patients (
    patient_id SERIAL PRIMARY KEY,
    patient_name VARCHAR(50) NOT NULL,
    date_of_birth DATE,
    contact_number VARCHAR(15)
);

CREATE TABLE ConsentForms (
    consent_id SERIAL PRIMARY KEY,
    patient_id INT REFERENCES Patients(patient_id),
    consent_date DATE,
    consent_given BOOLEAN
);

CREATE TABLE Regulations (
    regulation_id SERIAL PRIMARY KEY,
    regulation_name VARCHAR(100) NOT NULL,
    description TEXT,
    regulation_date DATE
);

CREATE TABLE Patient (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(50),
    date_of_birth DATE,
    consent_given BOOL
);

CREATE TABLE Regulations (
    reg_id INT PRIMARY KEY,
    reg_name VARCHAR(50),
    reg_description TEXT
);
