tables.table_schema:
CREATE TABLE patient_images (
    patient_id UUID,
    image_id UUID,
    image_data BYTEA,
    PRIMARY KEY (patient_id, image_id)
);

CREATE TABLE patient_results (
  patient_id INT PRIMARY KEY,
  diagnosis TEXT,
  imaging_results BYTEA,
  treatment_plan TEXT
);

CREATE TABLE medical_staff (
  staff_id INT PRIMARY KEY,
  staff_name TEXT,
  profession TEXT
);

CREATE TABLE robotic_systems (
  robot_id INT PRIMARY KEY,
  robot_type TEXT,
  manufacturer TEXT
);

CREATE TABLE imaging_centers (
  center_id INT PRIMARY KEY,
  center_name TEXT,
  location TEXT,
  capacity INT
);

CREATE TABLE Patients (
  patient_id UUID PRIMARY KEY,
  patient_name VARCHAR(50),
  patient_age INT,
  patient_gender VARCHAR(10)
);

CREATE TABLE ImagingResults (
  result_id UUID PRIMARY KEY,
  image_data BYTEA,
  result_text TEXT,
  patient_id UUID REFERENCES Patients(patient_id)
);

CREATE TABLE HealthcareRobots (
  robot_id UUID PRIMARY KEY,
  robot_name VARCHAR(50),
  robot_model VARCHAR(50),
  robot_type VARCHAR(50)
);

CREATE TABLE Treatments (
  treatment_id UUID PRIMARY KEY,
  treatment_name VARCHAR(50),
  treatment_cost DECIMAL(10,2),
  robot_id UUID REFERENCES HealthcareRobots(robot_id)
);

CREATE TABLE RobotTasks (
  task_id UUID PRIMARY KEY,
  task_description TEXT,
  task_status VARCHAR(20),
  robot_id UUID REFERENCES HealthcareRobots(robot_id)
);
