CREATE TABLE PolicyTypes (
    id INT PRIMARY KEY,
    type_name VARCHAR(50) UNIQUE NOT NULL,
    description TEXT
);

CREATE TABLE premium_payments (
   payment_id SERIAL PRIMARY KEY,
   policy_id INT,
   payment_amount DECIMAL,
   payment_date DATE
);

CREATE TABLE policy_renewals (
   renewal_id SERIAL PRIMARY KEY,
   policy_id INT,
   renewal_date DATE,
