tables.table_schema:
CREATE TABLE UnderwritingCriteria (
    id INT PRIMARY KEY,
    criteria_name VARCHAR(255),
    criteria_description TEXT
);

CREATE TABLE PolicyApproval (
    policy_id INT PRIMARY KEY,
    underwriting_criteria_id INT,
    approval_status VARCHAR(50),
    FOREIGN KEY (underwriting_criteria_id) REFERENCES UnderwritingCriteria(id)
);

CREATE TABLE underwriting_criteria (
  criteria_id INT PRIMARY KEY,
  criteria_name TEXT NOT NULL,
  criteria_description TEXT
);

CREATE TABLE policy_approval (
  approval_id INT PRIMARY KEY,
  policy_id INT NOT NULL,
  approval_date DATE,
  decision TEXT
);

CREATE TABLE policy (
  policy_id INT PRIMARY KEY,
  policy_holder TEXT NOT NULL,
  policy_type TEXT,
  policy_amount DECIMAL
);

CREATE TABLE Underwriters (
  ID INT PRIMARY KEY,
  Name VARCHAR(50) NOT NULL,
  Department VARCHAR(50) NOT NULL
);

CREATE TABLE PolicyTypes (
  TypeID INT PRIMARY KEY,
  TypeName VARCHAR(50) NOT NULL
);

CREATE TABLE Criteria (
  CriteriaID INT PRIMARY KEY,
  CriteriaName VARCHAR(50) NOT NULL
);

CREATE TABLE Underwriters (
    underwriter_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    phone_number VARCHAR(15) NOT NULL
);

CREATE TABLE Criteria (
    criteria_id INT PRIMARY KEY,
    age_requirement INT NOT NULL,
    income_requirement DECIMAL(10,2) NOT NULL
);
