tables.table_schema:
CREATE TABLE insurance_agents (
    agent_id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    region VARCHAR(50) NOT NULL,
    performance_score FLOAT NOT NULL
);

CREATE TABLE incentive_schemes (
    scheme_id SERIAL PRIMARY KEY,
    agent_id INT REFERENCES insurance_agents(agent_id),
    bonus_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE agents (
    agent_id INT PRIMARY KEY,
    agent_name VARCHAR(50),
    agent_location VARCHAR(50),
    agent_performance_rating FLOAT
);

CREATE TABLE incentive_schemes (
    scheme_id INT PRIMARY KEY,
    scheme_name VARCHAR(50),
    scheme_description TEXT,
    incentive_amount FLOAT
);

CREATE TABLE agents (
  agent_id INT PRIMARY KEY,
  agent_name VARCHAR(50),
  hire_date DATE,
  territory VARCHAR(50)
);

CREATE TABLE incentives (
  incentive_id INT PRIMARY KEY,
  incentive_type VARCHAR(50),
  incentive_amount DECIMAL(10, 2),
  incentive_date DATE
);
