tables.table_schema:
CREATE TABLE verification_history (
	history_id UUID PRIMARY KEY,
	customer_id UUID REFERENCES customer_information(customer_id),
	verification_date DATE NOT NULL,
	verification_status VARCHAR(50) NOT NULL
);

CREATE TABLE customer_info (
    customer_id INT PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    government_id VARCHAR(20) UNIQUE NOT NULL,
    address VARCHAR(255) NOT NULL
);

CREATE TABLE verification_status (
    verification_id SERIAL PRIMARY KEY,
    customer_id INT REFERENCES customer_info(customer_id) ON DELETE CASCADE,
    verification_date DATE NOT NULL,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE verification_logs (
    log_id SERIAL PRIMARY KEY,
    verification_id INT REFERENCES verification_status(verification_id) ON DELETE CASCADE,
    log_details TEXT NOT NULL,
    log_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE verification_errors (
  error_id INT PRIMARY KEY,
  verification_id INT,
  error_message TEXT,
  FOREIGN KEY (verification_id) REFERENCES verification_history(verification_id)
);
