tables.table_schema:
CREATE TABLE cryptocurrency_prices (
    id INT PRIMARY KEY,
    currency_pair VARCHAR(10),
    price DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE exchanges (
    exchange_id INT PRIMARY KEY,
    exchange_name VARCHAR(50),
    country VARCHAR(50),
    rating INT
);

CREATE TABLE market_cap (
    crypto_id INT PRIMARY KEY,
    market_cap DECIMAL,
    volume DECIMAL,
    market_rank INT
);

CREATE TABLE cryptocurrency_prices (
    id INT PRIMARY KEY,
    currency VARCHAR(50),
    price DECIMAL(10, 2),
    recorded_at TIMESTAMP
);
