tables.table_schema:
CREATE TABLE compliance_data (
    id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    country VARCHAR(50) NOT NULL,
    regulation_type VARCHAR(50) NOT NULL
);

CREATE TABLE transaction_logs (
    log_id INT PRIMARY KEY,
    compliance_id INT NOT NULL,
    transaction_type VARCHAR(50) NOT NULL,
    amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE compliance_data (
    id INT PRIMARY KEY,
    transaction_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    compliance_status VARCHAR(50) NOT NULL
);

CREATE TABLE law_regulations (
    law_id INT PRIMARY KEY,
    law_name VARCHAR(100) NOT NULL,
    law_description VARCHAR(255) NOT NULL,
    law_category VARCHAR(50) NOT NULL
);

CREATE TABLE compliance_history (
    history_id INT PRIMARY KEY,
    transaction_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    compliance_status VARCHAR(50) NOT NULL
);

CREATE TABLE recording_regulatory_compliance_data ( 
    id INT PRIMARY KEY, 
    transaction_id INT, 
    date TIMESTAMP, 
    compliance_status VARCHAR 
);

CREATE TABLE law_and_regulations ( 
    id INT PRIMARY KEY, 
    law_id INT, 
    law_description TEXT, 
    jurisdiction VARCHAR 
);

CREATE TABLE compliance_audit_logs ( 
    id INT PRIMARY KEY, 
    entry_datetime TIMESTAMP, 
    user_id INT, 
    activity_description TEXT 
);

CREATE TABLE countries (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(50)
);

CREATE TABLE compliance_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    log_date TIMESTAMP,
    action_performed VARCHAR(255)
);
