tables.table_schema:
CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR,
  spending_habits VARCHAR,
  saving_goals VARCHAR
);

CREATE TABLE CustomerSpendingHabits (
  customer_id INT PRIMARY KEY,
  total_spending DECIMAL(10, 2),
  last_purchase_date DATE
);

CREATE TABLE SavingGoals (
  customer_id INT PRIMARY KEY,
  saving_goal DECIMAL(10, 2),
  target_date DATE
);

CREATE TABLE InvestmentPreferences (
  customer_id INT PRIMARY KEY,
  risk_tolerance VARCHAR(50),
  investment_type VARCHAR(50)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    spending_habits JSON
);
