tables.table_schema:
CREATE TABLE smart_contracts (
    contract_id INT PRIMARY KEY,
    contract_name VARCHAR(255) NOT NULL,
    contract_code TEXT NOT NULL
);

CREATE TABLE smart_contracts (
    contract_id INT PRIMARY KEY,
    contract_name VARCHAR(255),
    contract_code TEXT,
    deploy_date DATE
);

CREATE TABLE contract_templates (
    template_id INT PRIMARY KEY,
    template_name VARCHAR(255),
    template_code TEXT,
    template_description TEXT
);

CREATE TABLE SmartContractTemplates (
    contract_id INT PRIMARY KEY, 
    contract_name VARCHAR(255) NOT NULL, 
    contract_code TEXT NOT NULL
);

CREATE TABLE ContractDeployments (
    deployment_id INT PRIMARY KEY, 
    contract_id INT NOT NULL, 
    deployment_date TIMESTAMP NOT NULL, 
    deployed_by VARCHAR(255) NOT NULL
);

CREATE TABLE ContractExecution (
    execution_id INT PRIMARY KEY, 
    deployment_id INT NOT NULL, 
    execution_date TIMESTAMP NOT NULL, 
    executed_by VARCHAR(255) NOT NULL
);

CREATE TABLE BlockchainPlatform (
    platform_id INT PRIMARY KEY, 
    platform_name VARCHAR(255) NOT NULL, 
    platform_version VARCHAR(50) NOT NULL
);

CREATE TABLE smart_contracts (
    contract_id UUID PRIMARY KEY,
    contract_name VARCHAR(255) NOT NULL,
    code BLOB NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE contract_logs (
    log_id UUID PRIMARY KEY,
    event_id UUID REFERENCES contract_events(event_id),
    log_text TEXT NOT NULL,
    log_timestamp TIMESTAMP NOT NULL
);
