tables.table_schema:
CREATE TABLE analyst_ratings (
    symbol STRING,
    date DATE,
    analyst_name STRING,
    rating INTEGER
);

CREATE TABLE stock_news (
    date DATE,
    headline STRING,
    content STRING
);

CREATE TABLE CompanyInfo (
    symbol STRING,
    company_name STRING,
    sector STRING,
    industry STRING
);

CREATE TABLE AnalystRatings (
    symbol STRING,
    date DATE,
    rating STRING
);

CREATE TABLE InvestmentPortfolio (
    symbol STRING,
    shares INT,
    purchase_price DECIMAL
);

CREATE TABLE stock_information (
  symbol VARCHAR(10),
  company_name VARCHAR(100),
  sector VARCHAR(50),
  exchange VARCHAR(20)
);

CREATE TABLE trade_volume (
  symbol VARCHAR(10),
  date DATE,
  volume INT,
  trade_type VARCHAR(10)
);

CREATE TABLE market_trends (
  trend_id SERIAL PRIMARY KEY,
  trend_description TEXT
);

CREATE TABLE investment_decisions (
  decision_id SERIAL PRIMARY KEY,
  decision_description TEXT,
  symbol VARCHAR(10),
  date DATE
);
