tables.table_schema:
CREATE TABLE portfolio_recommendations (
  recommendation_id UUID PRIMARY KEY,
  user_id UUID,
  recommended_portfolio VARCHAR(255),
  recommended_funds JSONB
);

CREATE TABLE FundPerformance (
  fund_id INT PRIMARY KEY,
  fund_name VARCHAR(50),
  fund_type VARCHAR(50),
  performance DECIMAL
);

CREATE TABLE InvestmentOptions (
  option_id INT PRIMARY KEY,
  option_name VARCHAR(50),
  risk_level VARCHAR(50),
  expected_return DECIMAL
);

CREATE TABLE PortfolioRecommendations (
  portfolio_id INT PRIMARY KEY,
  recommended_name VARCHAR(50),
  composition_json JSON
);

CREATE TABLE FundManagers (
  manager_id INT PRIMARY KEY,
  manager_name VARCHAR(50),
  experience_years INT
);
