tables.table_schema:
CREATE TABLE regulations (
    id INT PRIMARY KEY,
    regulation_name STRING,
    description STRING
);

CREATE TABLE compliance_requirements (
    id INT PRIMARY KEY,
    requirement_name STRING,
    is_mandatory BOOL
);

CREATE TABLE investment_regulations (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    description TEXT NOT NULL,
    category VARCHAR(50) NOT NULL
);

CREATE TABLE compliance_requirements (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    details TEXT NOT NULL,
    deadline TIMESTAMP
);

CREATE TABLE Investment_Regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100) NOT NULL,
    description TEXT,
    effective_date DATE
);

CREATE TABLE Compliance_Requirements (
    requirement_id INT PRIMARY KEY,
    requirement_name VARCHAR(100) NOT NULL,
    description TEXT,
    deadline DATE
);

CREATE TABLE Investment_Entities (
    entity_id INT PRIMARY KEY,
    entity_name VARCHAR(100) NOT NULL,
    registration_date DATE,
    country VARCHAR(50)
);

CREATE TABLE investment_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(255) NOT NULL,
    description TEXT,
    compliance_due_date DATE
);

CREATE TABLE investment_firms (
    firm_id INT PRIMARY KEY,
    firm_name VARCHAR(255) NOT NULL,
    location VARCHAR(100),
    founding_date DATE
);

CREATE TABLE compliance_officers (
    officer_id INT PRIMARY KEY,
    officer_name VARCHAR(255) NOT NULL,
    email VARCHAR(100),
    phone_number VARCHAR(20)
);
