);

CREATE TABLE MarketConditions (
    condition_id SERIAL PRIMARY KEY,
    condition_name VARCHAR(50) NOT NULL,
    condition_description TEXT
);

CREATE TABLE MarketData (
    timestamp TIMESTAMP,
