tables.table_schema:
CREATE TABLE Property (
    id INT PRIMARY KEY,
    address VARCHAR(255) NOT NULL,
    owner_id INT NOT NULL,
    blockchain_hash VARCHAR(255) NOT NULL
);

CREATE TABLE Ownership (
    id INT PRIMARY KEY,
    property_id INT NOT NULL,
    owner_id INT NOT NULL,
    transfer_date DATE NOT NULL,
    blockchain_signature VARCHAR(255) NOT NULL
);

CREATE TABLE property_owners (
    owner_id UUID PRIMARY KEY,
    owner_name VARCHAR(100) NOT NULL,
    email_address VARCHAR(255) NOT NULL,
    phone_number VARCHAR(20)
);

CREATE TABLE properties (
    property_id UUID PRIMARY KEY,
    property_address VARCHAR(255) NOT NULL,
    property_type VARCHAR(50),
    owner_id UUID REFERENCES property_owners(owner_id)
);

CREATE TABLE blockchain_records (
    block_id UUID PRIMARY KEY,
    transaction_id UUID REFERENCES property_transactions(transaction_id),
    block_hash TEXT NOT NULL,
    previous_block_hash TEXT
);

CREATE TABLE real_estate_owners (
    owner_id UUID primary key,
    owner_name VARCHAR(50),
    property_address VARCHAR(100),
    blockchain_transaction_id UUID
);

CREATE TABLE property_listings (
    listing_id UUID primary key,
    property_address VARCHAR(100),
    listing_price DECIMAL(12, 2),
    listing_date TIMESTAMP
);

CREATE TABLE blockchain_transactions (
    transaction_id UUID primary key,
    sender_address VARCHAR(100),
    receiver_address VARCHAR(100),
    transaction_date TIMESTAMP
);

CREATE TABLE property_history (
    history_id UUID primary key,
    property_address VARCHAR(100),
    event_description TEXT,
    event_date TIMESTAMP
);
