tables.table_schema:
CREATE TABLE BlockchainData (
    record_id SERIAL PRIMARY KEY,
    hash VARCHAR(255) NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    previous_hash VARCHAR(255)
);

CREATE TABLE SensorLocation (
    sensor_id SERIAL PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL
);

CREATE TABLE blockchain_data (
    block_id INT PRIMARY KEY,
    data_hash VARCHAR(256),
    previous_block_id INT,
    timestamp TIMESTAMP
);

CREATE TABLE sustainability_practices (
    practice_id INT PRIMARY KEY,
    practice_name VARCHAR(100),
    description TEXT,
    verification_status BOOLEAN
);

CREATE TABLE sensor_blockchain_mapping (
    sensor_id INT,
    block_id INT,
    PRIMARY KEY (sensor_id, block_id)
);

CREATE TABLE blockchain_network (
    block_id SERIAL PRIMARY KEY,
    hash_value VARCHAR(64) NOT NULL,
    previous_hash VARCHAR(64) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE sustainability_practices (
    practice_id SERIAL PRIMARY KEY,
    practice_name VARCHAR(100) NOT NULL,
    description TEXT NOT NULL
);
