tables.table_schema:
CREATE TABLE stock_volume (
  symbol VARCHAR(10),
  date DATE,
  volume INT
);

CREATE TABLE stock_metadata (
  symbol VARCHAR(10),
  company_name VARCHAR(100),
  sector VARCHAR(50),
  industry VARCHAR(50)
);

CREATE TABLE stock_company (
    ticker VARCHAR(10),
    company_name VARCHAR(100)
);

CREATE TABLE stock_industry (
    industry_id VARCHAR(10),
    industry_name VARCHAR(50)
);

CREATE TABLE stock_exchange (
    exchange_id VARCHAR(10),
    exchange_name VARCHAR(50)
);

CREATE TABLE company_info (
    symbol VARCHAR(10),
    company_name VARCHAR(100),
    sector VARCHAR(50),
    industry VARCHAR(50)
);

CREATE TABLE stock_trends (
    symbol VARCHAR(10),
    date DATE,
    trend_score FLOAT,
    trend_direction VARCHAR(10)
);

CREATE TABLE stock_volume (
    symbol VARCHAR(10),
    date DATE,
    volume INT,
    avg_volume INT
);

CREATE TABLE stock_dividends (
    symbol VARCHAR(10),
    ex_dividend_date DATE,
    dividend_amount DECIMAL(10,2),
    payment_date DATE
);
