tables.table_schema:
CREATE TABLE FinancialMarketNews (
    news_id SERIAL PRIMARY KEY,
    news_title TEXT,
    news_content TEXT,
    publish_date TIMESTAMP
);

CREATE TABLE SentimentAnalysis (
    analysis_id SERIAL PRIMARY KEY,
    news_id INT REFERENCES FinancialMarketNews(news_id),
    sentiment_score FLOAT,
    sentiment_category TEXT
);

CREATE TABLE TradingAlgorithms (
    algorithm_id SERIAL PRIMARY KEY,
    algorithm_name TEXT,
    algorithm_description TEXT,
    algorithm_type TEXT
);

CREATE TABLE financial_news_data (
    news_id UUID PRIMARY KEY,
    news_title TEXT,
    publication_date TIMESTAMP,
    sentiment_score FLOAT
);

CREATE TABLE stock_prices (
    stock_symbol TEXT PRIMARY KEY,
    company_name TEXT,
    current_price NUMERIC,
    change_percent NUMERIC
);

CREATE TABLE trading_algorithms (
    algo_id UUID PRIMARY KEY,
    algo_name TEXT,
    strategy TEXT
);

CREATE TABLE news_sentiment_analysis (
    analysis_id UUID PRIMARY KEY,
    news_id UUID,
    sentiment_score FLOAT,
    analysis_date TIMESTAMP
);

CREATE TABLE financial_news (
    news_id INT PRIMARY KEY,
    news_title TEXT,
    news_content TEXT,
    news_timestamp TIMESTAMP
);

CREATE TABLE trading_algorithms (
    algorithm_id INT PRIMARY KEY,
    algorithm_name VARCHAR(50),
    algorithm_description TEXT
);
