tables.table_schema:
CREATE TABLE traders (
	trader_id INT,
	trader_name VARCHAR(50),
	trader_location VARCHAR(50),
	trader_rating INT
);

CREATE TABLE anomalies (
	a_nomaly_id INT,
	a_type VARCHAR(20),
	a_timestamp TIMESTAMP,
	anomaly_description TEXT
);

CREATE TABLE analysis_results (
	result_id INT,
	result_description TEXT,
	result_timestamp TIMESTAMP
);

CREATE TABLE TradingPatterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(50),
    description TEXT
);

CREATE TABLE Anomalies (
    anomaly_id INT PRIMARY KEY,
    anomaly_type VARCHAR(50),
    anomaly_description TEXT
);
