tables.table_schema:
CREATE TABLE market_trends ( 
    date DATE, 
    economic_indicator VARCHAR(255), 
    macroeconomic_data FLOAT, 
    prediction FLOAT 
);

CREATE TABLE macroeconomic_data (
    market_trend_id SERIAL PRIMARY KEY,
    indicator_name VARCHAR(100) NOT NULL,
    data_value FLOAT NOT NULL,
    data_timestamp TIMESTAMP
);

CREATE TABLE machine_learning_models (
    model_id SERIAL PRIMARY KEY,
    model_name VARCHAR(100) NOT NULL,
    algorithm_used VARCHAR(50) NOT NULL,
    training_data_size INT NOT NULL,
    training_time INTERVAL
);

CREATE TABLE Economic_Indicators (
    indicator_id INT PRIMARY KEY,
    indicator_name VARCHAR(255),
    indicator_value FLOAT,
    indicator_date DATE
);

CREATE TABLE Market_Trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(255),
    trend_description TEXT,
    trend_date DATE
);

CREATE TABLE Machine_Learning_Models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255),
    model_type VARCHAR(50),
    model_accuracy FLOAT
);

CREATE TABLE MacroeconomicData (
    id INT PRIMARY KEY,
    indicator_name VARCHAR(255),
    data_date DATE,
    value NUMERIC
);

CREATE TABLE EconomicIndicators (
    id INT PRIMARY KEY,
    indicator_name VARCHAR(255),
    value NUMERIC,
    date_published DATE
);

CREATE TABLE MarketTrends (
    id INT PRIMARY KEY,
    trend_name VARCHAR(255),
    trend_date DATE,
    trend_value NUMERIC
);

CREATE TABLE MachineLearningModels (
    id INT PRIMARY KEY,
    model_name VARCHAR(255),
    algorithm VARCHAR(255),
    accuracy NUMERIC
);
