tables.table_schema:
CREATE TABLE digital_identities (
    id INT PRIMARY KEY,
    identity_hash STRING
);

CREATE TABLE blockchain_data (
    block_id INT PRIMARY KEY,
    transaction_id INT,
    identity_id INT,
    data JSONB
);

CREATE TABLE blockchain_accounts (
    account_id INT PRIMARY KEY,
    account_address VARCHAR(255) NOT NULL,
    account_balance DECIMAL(18, 2) DEFAULT 0.00,
    account_created_at TIMESTAMP DEFAULT NOW()
);

CREATE TABLE digital_identities (
    id SERIAL PRIMARY KEY,
    user_id UUID NOT NULL,
    blockchain_address VARCHAR(255) NOT NULL,
    public_key TEXT NOT NULL
);

CREATE TABLE authentication_logs (
    log_id SERIAL PRIMARY KEY,
    user_id UUID NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    event VARCHAR(50) NOT NULL
);

CREATE TABLE verification_requests (
    request_id SERIAL PRIMARY KEY,
    user_id UUID NOT NULL,
    request_date DATE NOT NULL,
    status VARCHAR(20) NOT NULL
);

CREATE TABLE blockchain_integration (
    integration_id SERIAL PRIMARY KEY,
    blockchain_name VARCHAR(50) NOT NULL,
    api_key VARCHAR(100) NOT NULL,
    integration_type VARCHAR(20) NOT NULL
);

CREATE TABLE digital_identities (
    id SERIAL PRIMARY KEY,
    wallet_address VARCHAR(255) NOT NULL,
    blockchain_hash VARCHAR(255) NOT NULL,
    identity_data JSONB
);

CREATE TABLE authentication_logs (
    log_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    action VARCHAR(50) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE verification_requests (
    verification_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    request_details JSONB,
    status VARCHAR(50) NOT NULL
);

CREATE TABLE user_profiles (
    user_id SERIAL PRIMARY KEY,
    full_name VARCHAR(100) NOT NULL,
    email VARCHAR(255) UNIQUE,
    phone_number VARCHAR(15)
);

CREATE TABLE access_tokens (
    token_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    token_value VARCHAR(255) NOT NULL,
    expiration_date TIMESTAMP NOT NULL
);
