tables.table_schema:
CREATE TABLE campaign_funding (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    amount_raised DECIMAL
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    raised_amount DECIMAL
);

CREATE TABLE campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE campaign_totals (
    campaign_id INT PRIMARY KEY,
    total_amount_raised DECIMAL,
    FOREIGN KEY (campaign_id) REFERENCES campaigns(campaign_id)
);

CREATE TABLE campaign (
    campaign_id UUID PRIMARY KEY,
    campaign_name VARCHAR(255) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);

CREATE TABLE donor (
    donor_id UUID PRIMARY KEY,
    donor_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE donation (
    donation_id UUID PRIMARY KEY,
    campaign_id UUID REFERENCES campaign(campaign_id),
    donor_id UUID REFERENCES donor(donor_id),
    amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE campaign_totals (
    campaign_id UUID REFERENCES campaign(campaign_id),
    total_amount_raised DECIMAL(10, 2) NOT NULL
);
