tables.table_schema:
CREATE TABLE legal_documents (
    document_id INT PRIMARY KEY,
    document_name TEXT,
    document_content TEXT,
    document_hash TEXT
);

CREATE TABLE digital_signatures (
    signature_id INT PRIMARY KEY,
    document_id INT,
    signer_name TEXT,
    signature_hash TEXT,
    FOREIGN KEY (document_id) REFERENCES legal_documents(document_id)
);

CREATE TABLE LegalDocuments (
	document_id INT PRIMARY KEY,
	document_name VARCHAR(255),
	document_content TEXT,
	document_sha256 VARCHAR(64)
);

CREATE TABLE legal_documents (
    document_id INT PRIMARY KEY,
    document_content TEXT,
    digital_signature TEXT
);

CREATE TABLE contract_management (
    contract_id INT PRIMARY KEY,
    contract_title TEXT,
    client_name TEXT,
    signing_date DATE
);

CREATE TABLE blockchain_platform (
    platform_id INT PRIMARY KEY,
    platform_name TEXT,
    platform_version TEXT,
    deploy_date DATE
);

CREATE TABLE signature_logs (
    log_id INT PRIMARY KEY,
    document_id INT,
    signature_status TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE LegalDocuments (
	document_id UUID PRIMARY KEY,
	document_name VARCHAR(255) NOT NULL,
	document_content TEXT NOT NULL,
	document_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE Users (
	user_id UUID PRIMARY KEY,
	user_name VARCHAR(100) NOT NULL,
	user_email VARCHAR(255) NOT NULL,
	user_role VARCHAR(50) NOT NULL
);

CREATE TABLE BlockchainTransactions (
	transaction_id UUID PRIMARY KEY,
	transaction_type VARCHAR(100) NOT NULL,
	transaction_data JSONB NOT NULL,
	transaction_timestamp TIMESTAMP NOT NULL
);
