tables.table_schema:
CREATE TABLE text_data_for_nlp (
    id SERIAL PRIMARY KEY,
    text_data VARCHAR(255) NOT NULL,
    task_type VARCHAR(50) NOT NULL
);

CREATE TABLE Document (id UUID PRIMARY KEY,
    text TEXT,
    language TEXT,
    created_at TIMESTAMP
);

CREATE TABLE Token (id UUID PRIMARY KEY,
    value TEXT,
    document_id UUID,
    created_at TIMESTAMP
);

CREATE TABLE Entity (id UUID PRIMARY KEY,
    name TEXT,
    token_id UUID,
    created_at TIMESTAMP
);

CREATE TABLE text_documents (
   id INT PRIMARY KEY,
   document_text TEXT
);

CREATE TABLE sentences (
   id INT PRIMARY KEY,
   document_id INT,
   sentence_text TEXT
);

CREATE TABLE words (
   id INT PRIMARY KEY,
   sentence_id INT,
   word_text TEXT
);

CREATE TABLE word_tags (
   id INT PRIMARY KEY,
   word_id INT,
   tag TEXT
);

CREATE TABLE documents_metadata (
   id INT PRIMARY KEY,
   document_id INT,
   metadata_key TEXT,
   metadata_value TEXT
);
